import React, {Component} from 'react';
import {Link} from "react-router-dom";
import url from "../url";


class Release extends Component {
    constructor(props){
        super(props)
        this.state={
            img1:{},
            img2:{}
        }
    }
    componentDidMount() {
        this.getImg1()
    }
    getImg1= (event)=>{
        fetch(`${url}/api/conference`)
            .then(res=>res.json())
            .then((data)=>{
                console.log(data)
                this.setState({
                    img1:data.data
                })
            })
    }

    render() {
        return (
            <div style={{width:'100%'}}>
                <img src={require('../../common/images/fabu.jpg')} width={'100%'} alt=""/>
                <div>
                    <img src={require('../../common/images/jingxuan.jpg')} width={'100%'} style={{magin:'6px 0'}} alt=""/>
                </div>
            </div>
        );
    }
}

export default Release;
