import React, {Component} from 'react';
import {Link,Redirect} from "react-router-dom";

import {TabBar } from "antd-mobile";
import './index.css'

class Footer extends Component {

    constructor(props) {
        super(props);
        this.state = {
            selectedTab: 'redTab',
            hidden: false,
            fullScreen: false,
        };
    }
    render() {
        return (
            <div className={'footer'} >
                <TabBar
                    unselectedTintColor="#949494"
                    tintColor="red"
                    barTintColor="white"
                    hidden={this.state.hidden}
                >
                        <TabBar.Item
                            title="首页"
                            key="shouye"
                            icon={<i className={'iconfont icon-shouye1'}/>}
                            selectedIcon={<i className={'iconfont icon-shouye1'}/>}
                            selected={this.state.selectedTab === 'blueTab'}
                            onPress={() => {
                                this.setState({
                                    selectedTab: 'blueTab',
                                });
                            }}
                            data-seed="logId"
                        >
                        </TabBar.Item>
                        <TabBar.Item
                            icon={
                                <i className={'iconfont icon-fenlei'}/>
                            }
                            selectedIcon={<i className={'iconfont icon-fenlei'}/>}
                            title="分类"
                            key="fenlei"
                            badge={'new'}
                            selected={this.state.selectedTab === 'redTab'}
                            onPress={() => {
                                this.setState({
                                    selectedTab: 'redTab',
                                });
                            }}
                            data-seed="logId1"
                        >
                        </TabBar.Item>
                    {/*<Link to={'/shopcar'}>*/}
                        <TabBar.Item
                            icon={<i className={'iconfont icon-gouwuche-01'}/>}
                            selectedIcon={<i className={'iconfont icon-gouwuche-01'}/>}
                            title="购物车"
                            key="gouwuc"
                            badge={1}
                            selected={this.state.selectedTab === 'greenTab'}
                            onPress={() => {
                                console.log('1');
                                this.setState({
                                    selectedTab: 'greenTab',
                                });
                                return <Redirect to={'/shopcar'}/>
                            }}

                        >

                        </TabBar.Item>

                    {/*</Link>*/}

                    <TabBar.Item
                        icon={
                            <i className={'iconfont icon-gerenzhongxin'}/>
                        }
                        selectedIcon={<i className={'iconfont icon-gerenzhongxin'}/>}
                        title="我的"
                        key="my"
                        dot
                        selected={this.state.selectedTab === 'yellowTab'}
                        onPress={() => {
                            this.setState({
                                selectedTab: 'yellowTab',
                            });
                        }}
                    >
                    </TabBar.Item>
                </TabBar>
            </div>
        );
    }
}

export default Footer;
