import React, {Component} from 'react';
import {List,Stepper , Checkbox,Toast} from "antd-mobile";
import {Link} from "react-router-dom";
import {connect} from "react-redux";

import {offline} from "../../common/js/toast/toast";
import Top from "../Header/top";
import './shopcar.css'
import url from "../util";
import {addgood,delgood,getgoodsnum} from "../../actions/cart";
import {shopTools} from "../util";

// const customIcon = () => (
//     <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 64 64" className="am-icon am-icon-md">
//         <path fillRule="evenodd" d="M59.177 29.5s-1.25 0-1.25 2.5c0 14.47-11.786 26.244-26.253 26.244C17.206 58.244 5.417 46.47 5.417 32c0-13.837 11.414-25.29 25.005-26.26v6.252c0 .622-.318 1.635.198 1.985a1.88 1.88 0 0 0 1.75.19l21.37-8.545c.837-.334 1.687-1.133 1.687-2.384C55.425 1.99 53.944 2 53.044 2h-21.37C15.134 2 1.667 15.46 1.667 32c0 16.543 13.467 30 30.007 30 16.538 0 29.918-13.458 29.993-30 .01-2.5-1.24-2.5-1.24-2.5h-1.25" />
//     </svg>
// );
//购物车
class ShopCar extends Component {
    constructor(props){
        super(props);
        this.state={
            goods:[],
            price:0,
            count:0
        }
        // this.totalPrice = this.totalPrice.bind(this)
    }
    componentDidMount() {
        setTimeout(() => {
            Toast.hide();
        }, 3000);
        this.getdata();
    }


    getdata= ()=>{
        let {buyCount} = this.props.car;
        let {goods} = this.state;
        let shopids = Object.keys(buyCount);
        shopids.forEach((item,index)=>{
            fetch(`${url}/api/buygoods/`+item)
                .then(res=>res.json())
                .then((data)=>{
                    data['num'] = buyCount[item];   //存储没见商品的数量
                    goods.push(data);
                    this.setState({
                        goods
                    },)
                })
        });
        this.getResult()
    };
    getResult= (event)=>{
        let time = setTimeout((event)=>{
            clearTimeout(time);
            this.totalPrice()
        },200)
    };

    totalPrice= ()=>{
        let price = 0;
        let count = 0;
        let goods = this.state.goods;
       for(let key of goods){
            price += key.num * key.price;
           count += key.num;
       }
        this.setState({
            price,
            count
        },(event)=>{
            console.log(this.state.goodsTotalCount);
        })
    };
    updateNum=(val,index)=>{
       if(val === 10){
           offline('最多购买10件');
           return
       }
        let {goods} = this.state;
        let {addgood} = this.props;
       let value = goods[index].num;
        let id = goods[index].shopid;
        if(val > value){
           goods[index].num++;
           this.setState({
               goods
           });
           addgood({
                id,
               num:1
           })
       }else {
             if(goods[index].num){
                 goods[index].num--;
                 this.setState({
                     goods
                 });
                 addgood({
                     id,
                     num:-1
                 })
             }
        }
    };
    render() {
        let {goods,price,count} = this.state;
        return (
            <div>
                <Top/>
               <div className={'shopcar'}>
                   <List>
                       {
                           goods.length > 0 && (
                               goods.map((item,index)=>{
                                   return (
                                       <List.Item
                                           key={index}
                                           thumb={
                                               <Checkbox/>
                                           }
                                           wrap={true}
                                       >
                                           <img className={''} src={item.picurl} width={'100%'} height={'100%'} alt={item.title}/>
                                           <div >
                                               <div className={'buy_title'} >{item.title}</div>
                                               <div><span style={{marginRight:'5px'}}>售价:</span><span style={{color:'#797979',fontSize:'0.25rem'}}>{item.symbol}{item.price}</span></div>
                                               <div className={'num'} >
                                                   <Stepper
                                                       showNumber
                                                       max={10}
                                                       min={0}
                                                       defaultValue={item.num}
                                                       onChange={(val)=>this.updateNum(val,index)}
                                                   />
                                                   <i className={'iconfont icon-shanchu'}/>
                                               </div>
                                           </div>
                                       </List.Item>

                                   )
                               })
                           )
                       }
                   </List>
                    <div className={'buy_footer'}>
                        <div className={'buy_span'}><span >共件 金额：</span><div>1999 <span>元</span></div></div>
                        <div style={{color:'#555',fontSize:'0.35rem'}}><Link to={'/'}>继续购物</Link></div>
                        <div><button className={'buy_button'}> 去结算</button></div>
                    </div>
               </div>

            </div>
        );
    }
}
const mapStateToProps = (state)=>{
    return {car:state}
};
const mapDispatchToProps = {
    addgood,
    delgood,
    getgoodsnum
};
ShopCar = connect(mapStateToProps,mapDispatchToProps)(ShopCar);
export default ShopCar;
