import React, {Component} from 'react';
import {List, Checkbox, Flex} from "antd-mobile";
import { InputNumber } from 'antd';
import './shopcar.css'


//购物车
class ShopCar extends Component {
    render() {

        return (
            <div>
               <div className={'shopcar'}>
                   <div className={'shop_content'}>
                       <img id={'gouwu'} src={require('../../common/images/gouwuc.png')} width={'100%'} alt=""/>
                       <span >购物车还是空的</span>
                       <button>去逛逛</button>
                   </div>
                   <List>
                       <List.Item
                            thumb={
                                <Checkbox/>
                            }
                       >
                           <img className={''} src={require('../../common/images/buy_img.jpg')} width={'100%'} height={'100%'} alt=""/>

                           <div >
                               <div style={{width:'4rem',height:'auto',wordWrap:'break_word',wordBreak:'break-all'}}>小米MIX 2S 全网通版 6GB内存 陶瓷标准版 128GB 黑色 128GB</div>
                               <div><span style={{marginRight:'5px'}}>售价:</span><span style={{color:'#797979',fontSize:'0.25rem'}}>￥1999</span></div>
                               <div className={'num'} ><InputNumber min={1} max={10} defaultValue={1}  /><i className={'iconfont icon-shanchu'}/></div>
                           </div>

                       </List.Item>
                   </List>
<div className={'buy_footer'}>
    <div><span>共1件 金额：</span><div>1999 <span>元</span></div></div>
    <div style={{color:'#555'}}>继续购物</div>
    <div><button className={'buy_button'}> 去结算</button></div>
</div>


               </div>

            </div>
        );
    }
}

export default ShopCar;
