import React, {Component} from 'react';
import {Link} from "react-router-dom";

import {TabBar } from "antd-mobile";
import './index.css'

class Footer extends Component {

    constructor(props) {
        super(props);
        this.state = {
            selectedTab: 'redTab',
            hidden: false,
            fullScreen: false,
        };
    }
    renderContent(pageText) {
        return (
            <div style={{ backgroundColor: 'white', height: '100%', textAlign: 'center' }}>
                <div style={{ paddingTop: 60 }}>Clicked “{pageText}” tab， show “{pageText}” information</div>
                <a style={{ display: 'block', marginTop: 40, marginBottom: 20, color: '#108ee9' }}
                   onClick={(e) => {
                       e.preventDefault();
                       this.setState({
                           hidden: !this.state.hidden,
                       });
                   }}
                >
                    Click to show/hide tab-bar
                </a>
                <a style={{ display: 'block', marginBottom: 600, color: '#108ee9' }}
                   onClick={(e) => {
                       e.preventDefault();
                       this.setState({
                           fullScreen: !this.state.fullScreen,
                       });
                   }}
                >
                    Click to switch fullscreen
                </a>
            </div>
        );
    }
    render() {
        let aList = document.querySelectorAll('.footer ul li a'),
            length = aList.length,
            num = 0;

        for(let i = 0;i<length;i++){
            aList[i].click = function () {
                aList[i].classList.add('active')
            }
        }
        return (
            <div className={'footer'}>
                <TabBar
                    unselectedTintColor="#949494"
                    tintColor="#33A3F4"
                    barTintColor="white"
                    hidden={this.state.hidden}
                >
                    <TabBar.Item
                        title="Life"
                        key="Life"
                        icon={<i className={'iconfont icon-shouye1'}/>
                        }
                        selectedIcon={<div style={{
                            width: '22px',
                            height: '22px',
                            background: 'url(https://zos.alipayobjects.com/rmsportal/iSrlOTqrKddqbOmlvUfq.svg) center center /  21px 21px no-repeat' }}
                        />
                        }
                        selected={this.state.selectedTab === 'blueTab'}
                        badge={1}
                        onPress={() => {
                            this.setState({
                                selectedTab: 'blueTab',
                            });
                        }}
                        data-seed="logId"
                    >
                        {this.renderContent('首页')}
                    </TabBar.Item>
                    <TabBar.Item
                        icon={
                            <i className={'iconfont icon-fenlei'}/>
                        }
                        selectedIcon={
                            <div style={{
                                width: '22px',
                                height: '22px',
                                background: 'url(https://gw.alipayobjects.com/zos/rmsportal/ekLecvKBnRazVLXbWOnE.svg) center center /  21px 21px no-repeat' }}
                            />
                        }
                        title="Koubei"
                        key="Koubei"
                        badge={'new'}
                        selected={this.state.selectedTab === 'redTab'}
                        onPress={() => {
                            this.setState({
                                selectedTab: 'redTab',
                            });
                        }}
                        data-seed="logId1"
                    >
                        {this.renderContent('分类')}
                    </TabBar.Item>
                    <TabBar.Item
                        icon={
                            <i className={'iconfont icon-gouwuche-01'}/>
                        }
                        selectedIcon={
                            <div style={{
                                width: '22px',
                                height: '22px',
                                background: 'url(https://zos.alipayobjects.com/rmsportal/IIRLrXXrFAhXVdhMWgUI.svg) center center /  21px 21px no-repeat' }}
                            />
                        }
                        title="Friend"
                        key="Friend"
                        dot
                        selected={this.state.selectedTab === 'greenTab'}
                        onPress={() => {
                            this.setState({
                                selectedTab: 'greenTab',
                            });
                        }}
                    >
                        {this.renderContent('购物车')}
                    </TabBar.Item>
                    <TabBar.Item
                        icon={{ uri: 'https://zos.alipayobjects.com/rmsportal/asJMfBrNqpMMlVpeInPQ.svg' }}
                        selectedIcon={{ uri: 'https://zos.alipayobjects.com/rmsportal/gjpzzcrPMkhfEqgbYvmN.svg' }}
                        title="My"
                        key="my"
                        selected={this.state.selectedTab === 'yellowTab'}
                        onPress={() => {
                            this.setState({
                                selectedTab: 'yellowTab',
                            });
                        }}
                    >
                        {this.renderContent('My')}
                    </TabBar.Item>
                </TabBar>

                <ul>
                    <li><Link to="/" className={'active'}>
                        <i className={'iconfont icon-shouye1'}/>
                        <div>首页</div>
                    </Link></li>
                    <li><Link to="/myInfo">
                        <div>
                            <i className={'iconfont icon-fenlei'}/>
                            <div>分类</div></div>
                    </Link></li>
                    <li><Link to="/shopcar">
                        <div>
                            <i className={'iconfont icon-gouwuche-01'}/>
                            <div>购物车</div></div>
                    </Link></li>
                    <li><Link to="/myInfo">
                        <div>
                            <i className={'iconfont icon-gerenzhongxin'}/>
                            <div>我的</div></div>
                    </Link></li>
                </ul>
               {/* <a href="">
                    <div className={'info'}>
                        <i className={'iconfont icon-shouye1'}/>
                        <div>首页</div>
                    </div>
                </a>
                <a href="">
                    <div>
                        <i className={'iconfont icon-fenlei'}/>
                        <div>分类</div></div></a>
                <a href="">
                    <div>
                        <i className={'iconfont icon-gouwuche-01'}/>
                        <div>购物车</div></div></a>
                <a href="">
                    <div>
                        <i className={'iconfont icon-gerenzhongxin'}/>
                        <div>我的</div></div></a>*/}
            </div>
        );
    }
}

export default Footer;
