import React, {Component} from 'react';
import {List,Stepper , Checkbox, Flex} from "antd-mobile";
import {Link} from "react-router-dom";

import Top from "../Header/top";

import { InputNumber } from 'antd';
import './shopcar.css'


//购物车
class ShopCar extends Component {
    render() {

        return (
            <div>
                <Top/>
               <div className={'shopcar'}>
                   {/*<div className={'shop_content'}>*/}
                   {/*    <img id={'gouwu'} src={require('../../common/images/gouwuc.png')} width={'100%'} alt=""/>*/}
                   {/*    <span >购物车还是空的</span>*/}
                   {/*    <button>去逛逛</button>*/}
                   {/*</div>*/}
                   <List>
                       <List.Item
                            thumb={
                                <Checkbox/>
                            }
                            wrap={true}
                       >
                           <img className={''} src={require('../../common/images/buy_img.jpg')} width={'100%'} height={'100%'} alt=""/>
                           <div >
                               <div className={'buy_title'} >小米MIX 2S 全网通版 6GB内存 陶瓷标准版 128GB 黑色 128GB</div>
                               <div><span style={{marginRight:'5px'}}>售价:</span><span style={{color:'#797979',fontSize:'0.25rem'}}>￥1999</span></div>
                               <div className={'num'} >
                                   <Stepper
                                       // style={{ width: '100%', minWidth: '100px' }}
                                       showNumber
                                       max={10}
                                       min={0}
                                       defaultValue={0}

                                       onChange={this.onChange}
                                   />
                                   <i className={'iconfont icon-shanchu'}/>
                               </div>
                           </div>
                       </List.Item>
                   </List>
<div className={'buy_footer'}>
    <div className={'buy_span'}><span >共1件 金额：</span><div>1999 <span>元</span></div></div>
    <div style={{color:'#555',fontSize:'0.35rem'}}><Link to={'/'}>继续购物</Link></div>
    <div><button className={'buy_button'}> 去结算</button></div>
</div>


               </div>

            </div>
        );
    }
}

export default ShopCar;
