import React, {Component} from 'react';
import { Card, WingBlank ,List,Icon, ActionSheet,  WhiteSpace, Button, Toast} from 'antd-mobile';
import './goodsDetail.css'
import Back from "../back/back";
import AddCar from "../footer/addCar";
import url from "../url";
import {connect} from "react-redux";
import {addgood} from "../../actions/cart";

const Item = List.Item;


// @connect(
//     state=>({abc:state}),
//     {addgood}
// )
class GoodsDetail extends Component {
    constructor(props) {
        super(props);
        this.state={
            data:{}
        };
        console.log(this.props)
    }
    componentDidMount() {
        let shopid = this.props.match.params.shopid;
        this.getGoodbyId(shopid)

        // console.log(this.props)
    }
    getGoodbyId=(shopid)=>{
        fetch(`${url}/api/buygoods/${shopid}`)
            .then(res=>res.json())
            .then((data)=>{
                if(data !== undefined){
                    this.setState({
                        data:data
                    },(event)=>{
                        console.log(this.state.data)
                    })
                    // dispatch({
                    //
                    // })
                }
                console.log(data)
            })
    };
    
    render() {
        let dat  = this.state.data;
        return (
            <div className={'goods'}>
                <Back/>
                <div className={'goods_img'}>
                    <img src={dat.picurl} height={'100%'} width={'100%'} alt=""/>
                </div>
                <div className={'goods_content'}>
                    <div className={'hea'}>
                        <div>秒杀价￥1999</div>
                        <div>倒计时 12:59:00</div>
                    </div>
                    <WingBlank size="lg">
                        <WhiteSpace size="lg" />
                        <Card>
                            <Card.Header
                                title={dat.title}
                            />
                            <Card.Body>
                                <div>{dat.des}</div>
                            </Card.Body>
                            <Card.Footer content={<div style={{display:'inline',color:'#ff4a00',fontSize:'0.5rem'}}>{dat.symbol}{dat.price}</div>}  />
                        </Card>
                        {/*<WhiteSpace size="lg" />*/}
                    </WingBlank>
                    <List >
                            <Item
                                thumb={require('../../common/images/已选.png')}
                                arrow="horizontal"
                                // onClick={this.showActionSheet}
                            >{dat.title}</Item>

                            <Item
                                thumb={require('../../common/images/已选.png')}
                                arrow="horizontal"
                                onClick={() => {}}
                            ><span>北京市</span><span>东城区</span><span style={{color:'red'}}>有现货</span>
                            </Item>
                      

                        <Item
className={'list_item'}
                            arrow="horizontal"
                            onClick={() => {}}
                        >
                            <span className={'span'}>
                                <Icon  className={'icon'} type={'check-circle-o'}/><span>小米自营</span>
                            </span >
                            <span className={'span'}>
                                <Icon className={'icon'} type={'check-circle-o'}/><span>小米发货</span>
                            </span>
                            <span className={'span'}>
                                <Icon  className={'icon'} type={'check-circle-o'}/><span>7天无理由退货</span>
                            </span>
                        </Item>

                    </List>
                </div>
                <AddCar/>
            </div>
        );
    }
}

export default connect(state=>state,{buyCount})(GoodsDetail) ;
