import React, {Component} from 'react';
import { Card, WingBlank ,List,Icon, ActionSheet,  WhiteSpace, Button, Toast} from 'antd-mobile';
import {Menu} from 'antd'
import './goodsDetail.css'
import data from "../../common/dataList";

import AddCar from "../footer/addCar";

const Item = List.Item;
const Brief = Item.Brief;

const isIPhone = new RegExp('\\biPhone\\b|\\biPod\\b', 'i').test(window.navigator.userAgent);
let wrapProps;
if (isIPhone) {
    wrapProps = {
        onTouchStart: e => e.preventDefault(),
    };
}

class GoodsDetail extends Component {

    constructor() {
        super();
        this.state = {
            clicked: 'none',
            clicked1: 'none',
            clicked2: 'none',
        };
    }
    dataList = [
        { url: 'OpHiXAcYzmPQHcdlLFrc', title: '发送给朋友' },
        { url: 'wvEzCMiDZjthhAOcwTOu', title: '新浪微博' },
        { url: 'cTTayShKtEIdQVEMuiWt', title: '生活圈' },
        { url: 'umnHwvEgSyQtXlZjNJTt', title: '微信好友' },
        { url: 'SxpunpETIwdxNjcJamwB', title: 'QQ' },
    ].map(obj => ({
        icon: <img src={`https://gw.alipayobjects.com/zos/rmsportal/${obj.url}.png`} alt={obj.title} style={{ width: 36 }} />,
        title: obj.title,
    }));

    showActionSheet = () => {
        const BUTTONS = ['Operation1', 'Operation2', 'Operation2', 'Delete', 'Cancel'];
        ActionSheet.showActionSheetWithOptions({
                options: BUTTONS,
                cancelButtonIndex: BUTTONS.length - 1,
                destructiveButtonIndex: BUTTONS.length - 2,
                // title: 'title',
                message: 'I am description, description, description',
                maskClosable: true,
                'data-seed': 'logId',
                wrapProps,
            },
            (buttonIndex) => {
                this.setState({ clicked: BUTTONS[buttonIndex] });
            });
    };
    showShareActionSheet = () => {
        ActionSheet.showShareActionSheetWithOptions({
                options: this.dataList,
                // title: 'title',
                message: 'I am description, description, description',
            },
            (buttonIndex) => {
                this.setState({ clicked1: buttonIndex > -1 ? this.dataList[buttonIndex].title : 'cancel' });
                // also support Promise
                return new Promise((resolve) => {
                    Toast.info('closed after 1000ms');
                    setTimeout(resolve, 1000);
                });
            });
    }

    showShareActionSheetMulpitleLine = () => {
        const data = [[...this.dataList, this.dataList[2]], [this.dataList[3], this.dataList[4]]];
        ActionSheet.showShareActionSheetWithOptions({
                options: data,
                message: 'I am description, description, description',
            },
            (buttonIndex, rowIndex) => {
                this.setState({ clicked2: buttonIndex > -1 ? data[rowIndex][buttonIndex].title : 'cancel' });
            });
    }

    showActionSheetBadge = () => {
        const BUTTONS = ['Operation1', 'Operation2', 'Operation3', 'Operation4', 'Operation5', 'Delete', 'Cancel'];
        const BADGES = [{
            index: 1,
            dot: true,
        }, {
            index: 2,
            text: 3100,
        }, {
            index: 3,
            text: '推荐',
        }, {
            index: 4,
            text: '···',
        }];
        ActionSheet.showActionSheetWithOptions({
                options: BUTTONS,
                cancelButtonIndex: BUTTONS.length - 1,
                destructiveButtonIndex: BUTTONS.length - 2,
                badges: BADGES,
                // title: 'title',
                message: 'I am description, description, description',
                maskClosable: true,
                'data-seed': 'logId',
                wrapProps,
            },
            (buttonIndex) => {
                this.setState({ clicked: BUTTONS[buttonIndex] });
            });
    };

    render() {
        return (
            <div className={'goods'}>
                <div className={'goods_img'}>
                    <img src={require('../../common/images/goodsD.jpg')} width={'100%'} alt=""/>
                </div>
                <div className={'goods_content'}>
                    <div className={'hea'}>
                        <div>秒杀价￥1999</div>
                        <div>倒计时 12:59:00</div>
                    </div>
                    <WingBlank size="lg">
                        <WhiteSpace size="lg" />
                        <Card>
                            <Card.Header
                                title="小米MIX 2S"
                            />
                            <Card.Body>
                                <div><span style={{color:'#ff4a00'}}>「6GB+128GB，特惠价1999元，享花呗6期免息」</span>陶瓷机身 手机中的艺术品 / 搭载高通骁龙845 年度旗舰处理器 / AI超感光双摄，DxO百分相机 / 高效 Qi 无线充电</div>
                            </Card.Body>
                            <Card.Footer content={<div style={{display:'inline',color:'#ff4a00',fontSize:'0.5rem'}}>￥1999</div>} extra={<span style={{display:'inline',color:'#ccc',textDecoration:' line-through',fontSize:'0.3rem',textAlign:'left'}}>￥2999</span>} />
                        </Card>
                        {/*<WhiteSpace size="lg" />*/}
                    </WingBlank>
                    <a href="">
                        <Menu
                            mode={"horizontal"}
                        >
                            {
                                // console.log(data)
                                data.map((item,index)=>{
                                    return (
                                        <Menu.Item >
                                            <img src={item.imag} alt=""/>
                                            <div>{item.nei_info}</div>
                                            <div>{item.wai_info}</div>
                                        </Menu.Item>
                                    )
                                })
                            }
                        </Menu>
                     {/*   <ul className={'xiaomi_peizhi'}>
                            <li><img src={require('../../common/images/rongliang.png')} alt=""/>
                                <div>CPU</div>
                                <div>5.99英才</div></li>
                            <li><img src={require('../../common/images/rongliang.png')} alt=""/>
                                <div>CPU</div>
                                <div>5.99英才</div></li>
                            <li><img src={require('../../common/images/rongliang.png')} alt=""/>
                                <div>CPU</div>
                                <div>5.99英才</div></li>
                            <li><img src={require('../../common/images/rongliang.png')} alt=""/>
                                <div>CPU</div>
                                <div>5.99英才</div></li>
                            <li><img src={require('../../common/images/rongliang.png')} alt=""/>
                                <div>CPU</div>
                                <div>5.99英才</div></li>
                            <li><img src={require('../../common/images/rongliang.png')} alt=""/>
