                            <li><img src={require('../../common/images/rongliang.png')} alt=""/>
                                <div>CPU</div>
                                <div>5.99英才</div></li>
                            <li><img src={require('../../common/images/rongliang.png')} alt=""/>
                                <div>CPU</div>
                                <div>5.99英才</div></li>
                            <li><img src={require('../../common/images/rongliang.png')} alt=""/>
                                <div>CPU</div>
                                <div>5.99英才</div></li>
                            <li><img src={require('../../common/images/rongliang.png')} alt=""/>
                                <div>CPU</div>
                                <div>5.99英才</div></li>
                        </ul>*/}
                    </a>

                    <List >
                            <Item
                                thumb={require('../../common/images/已选.png')}
                                arrow="horizontal"
                                onClick={this.showActionSheet}
                            >小米MIX 2S 6GB+128GB 黑色*1</Item>

                            <Item
                                thumb={require('../../common/images/已选.png')}
                                arrow="horizontal"
                                onClick={() => {}}
                            ><span>北京市</span><span>东城区</span><span style={{color:'red'}}>有现货</span>
                            </Item>
                      

                        <Item
className={'list_item'}
                            arrow="horizontal"
                            onClick={() => {}}
                        >
                            <span className={'span'}>
                                <Icon  className={'icon'} type={'check-circle-o'}/><span>小米自营</span>
                            </span >
                            <span className={'span'}>
                                <Icon className={'icon'} type={'check-circle-o'}/><span>小米发货</span>
                            </span>
                            <span className={'span'}>
                                <Icon  className={'icon'} type={'check-circle-o'}/><span>7天无理由退货</span>
                            </span>
                        </Item>

                    </List>
                </div>
                {/*<AddCar/>*/}
            </div>
        );
    }
}

export default GoodsDetail;
