import React, {Component} from 'react';
// import {} from "antd-mobile";
import './login.css'

class Login extends Component {
    render() {
        return (
            <div className={'login'}>
                <div className={'login_header'}>
                    <img src={require('../../common/images/logo1.png')} alt=""/>
                    <h2>小米账号登录</h2>
                </div>
                <div className={'login_midder'}>
                    <ul>
                        <li className={''}><span style={{color:'#767676',marginRight:'10px',fontSize:'0.3rem'}}>86 > </span><input type="text" placeholder={'手机号码'}/></li>
                        <li className={'list_item'}><input type="text"  placeholder={'短信验证码'}/><button>获取验证码</button></li>
                        <li><button>立即登录注册</button></li>
                    </ul>
                </div>
            </div>
        );
    }
}

export default Login;
