import React, {Component} from 'react';
import {Link} from "react-router-dom";
import url from "../url";


class Release extends Component {
    constructor(props){
        super(props)
        this.state={
            img1:{},
            img2:{}
        }
    }
    componentDidMount() {
        this.getImg1()
    }
    getImg1= (event)=>{
        fetch(`${url}/api/conference`)
            .then(res=>res.json())
            .then((data)=>{
                console.log(data);
                this.setState({
                    img1:data.data
                })
            });
        fetch(`${url}/api/conference`)
            .then(res=>res.json())
            .then((data)=>{
                console.log(data)
                this.setState({
                    img2:data.data
                })
            })
    };

    render() {
        let {img1,img2} = this.state;
        return (
            <div style={{width:'100%'}}>
                <Link to={`/`}>
                    <img src={img1.picurl} width={'100%'} alt={img1.title}/>
                </Link>

                <div>
                    <Link to={`/`}>
                        <img src={require('../../common/images/jingxuan.jpg')} width={'100%'} style={{magin:'6px 0'}} alt=""/>
                    </Link>

                </div>
            </div>
        );
    }
}

export default Release;
