import React, {Component} from 'react';
import {Link} from "react-router-dom";

import {TabBar } from "antd-mobile";
import './index.css'

class Footer extends Component {


    render() {
        let aList = document.querySelectorAll('.footer ul li a'),
            length = aList.length,
            num = 0;

        for(let i = 0;i<length;i++){
            aList[i].click = function () {
                aList[i].classList.add('active')
            }
        }
        return (
            <div className={'footer'}>
                <ul>
                    <li><Link to="/" className={'active'}>
                        <i className={'iconfont icon-shouye1'}/>
                        <div>首页</div>
                    </Link></li>
                    <li><Link to="/myInfo">
                        <div>
                            <i className={'iconfont icon-fenlei'}/>
                            <div>分类</div></div>
                    </Link></li>
                    <li><Link to="/myInfo">
                        <div>
                            <i className={'iconfont icon-gouwuche-01'}/>
                            <div>购物车</div></div>
                    </Link></li>
                    <li><Link to="/myInfo">
                        <div>
                            <i className={'iconfont icon-gerenzhongxin'}/>
                            <div>我的</div></div>
                    </Link></li>
                </ul>
               {/* <a href="">
                    <div className={'info'}>
                        <i className={'iconfont icon-shouye1'}/>
                        <div>首页</div>
                    </div>
                </a>
                <a href="">
                    <div>
                        <i className={'iconfont icon-fenlei'}/>
                        <div>分类</div></div></a>
                <a href="">
                    <div>
                        <i className={'iconfont icon-gouwuche-01'}/>
                        <div>购物车</div></div></a>
                <a href="">
                    <div>
                        <i className={'iconfont icon-gerenzhongxin'}/>
                        <div>我的</div></div></a>*/}
            </div>
        );
    }
}

export default Footer;
