import React, {Component} from 'react';
import './index.css'
import url from "../url";
class Index extends Component {
    state={
        datanav:[]
    };

    componentDidMount() {
        this.getNav();
    }
    getNav=()=>{
        fetch(`${url}/api/category`)
            .then(res=>res.json())
            .then((data)=>{
                if(data.status == 200){
                    this.setState({
                        datanav:data.data
                    })
                }
                console.log(data)
            })
    }
    render() {
        return (
            <div className={'fenleiNav'}>
                {
                    this.state.datanav.length > 0 &&this.state.datanav.map((item,index)=>{
                        return (
                            <a href="">
                                <img src={require('../../common/images/xinpin.jpg')} alt=""/>
                            </a>
                        )
                    })

                }


                {/*<div className={'nav_img'}>*/}
                {/*   */}
                {/*</div>*/}
            </div>
        );
    }
}

export default Index;
