import React, {Component} from 'react';
import {Link,Redirect} from "react-router-dom";
import {connect} from "react-redux";
import {TabBar } from "antd-mobile";
import './index.css'
import {getgoodsnum} from "../../actions/cart";
import ShopCar from "../shopCar/shopCar";

class Footer extends Component {

    constructor(props) {
        super(props);
        this.state = {
            selectedTab: 'redTab',
            hidden: false,
            fullScreen: false,
            num:0
        };
        console.log(this.props);
    }
    componentDidMount() {
        this.getshopNum()
    }

    getshopNum=()=>{
        let {num} = this.state;
        let shopNum = [];
        let {buyCount} = this.props.shopNum;
        let keys = Object.keys(buyCount);
        keys.forEach((item)=>{
            num += buyCount[item];
        });

        this.setState({
            num
        });
        console.log(buyCount[keys]);
    };
    render() {
        return (
            <div className={'footer'} >
                <TabBar
                    unselectedTintColor="#949494"
                    tintColor="red"
                    barTintColor="white"
                    hidden={this.state.hidden}
                >
                    <TabBar.Item
                        title={
                            <Link to={'/'}>首页</Link>
                        }
                        key="shouye"
                        icon={<i className={'iconfont icon-shouye1'}/>}
                        selectedIcon={<i className={'iconfont icon-shouye1'}/>}
                        selected={this.state.selectedTab === 'blueTab'}
                        onPress={() => {
                            this.setState({
                                selectedTab: 'blueTab',
                            });
                        }}
                        data-seed="logId"
                    >
                    </TabBar.Item>
                    <TabBar.Item
                        icon={
                            <i className={'iconfont icon-fenlei'}/>
                        }
                        selectedIcon={<i className={'iconfont icon-fenlei'}/>}
                        title={
                            <Link to={'/'}>分类</Link>
                        }
                        key="fenlei"
                        badge={'new'}
                        selected={this.state.selectedTab === 'redTab'}
                        onPress={() => {
                            this.setState({
                                selectedTab: 'redTab',
                            });
                        }}
                        data-seed="logId1"
                    >
                    </TabBar.Item>
                    <TabBar.Item
                        icon={<i className={'iconfont icon-gouwuche-01'}/>}
                        selectedIcon={<i className={'iconfont icon-gouwuche-01'}/>}
                        title={
                            <Link to={'/shopcar'}>购物车</Link>
                        }
                        key="gouwuc"
                        badge={1}
                        selected={this.state.selectedTab === 'greenTab'}
                        onPress={() => {
                            this.setState({
                                selectedTab: 'greenTab',
                            });
                        }}
                    >
                    </TabBar.Item>
                    <TabBar.Item
                        icon={
                            <i className={'iconfont icon-gerenzhongxin'}/>
                        }
                        selectedIcon={<i className={'iconfont icon-gerenzhongxin'}/>}
                        title={
                            <Link to={'/myInfo'}>我的</Link>
                        }
                        key="my"
                        dot
                        selected={this.state.selectedTab === 'yellowTab'}
                        onPress={() => {
                            this.setState({
                                selectedTab: 'yellowTab',
                            });

                        }}
                    >
                    </TabBar.Item>
                </TabBar>
            </div>
        );
    }
}
const mapStateToProps = (state)=>{
    return {shopNum:state}
};
const mapDispatchToProps = {
    getgoodsnum
};
Footer = connect(mapStateToProps,mapDispatchToProps)(Footer);
export default Footer;
