import React, {Component} from 'react';
import './index.css'
import url from "../url";
class Index extends Component {
    state={
        datanav:[]
    };

    componentDidMount() {
        this.getNav();
    }
    getNav=()=>{
        fetch(`${url}/api/banner`)
            .then(res=>res.json())
            .then((data)=>{
                if(data.status == 200){
                    this.setState({
                        dataBanner:data.data
                    })
                }
                console.log(data)
            })
    }
    render() {
        return (
            <div className={'fenleiNav'}>
                    <a href=""><img src={require('../../common/images/xinpin.jpg')} alt=""/></a>
                    <a href=""><img src={require('../../common/images/dazhong.jpg')} alt=""/></a>
                    <a href=""><img src={require('../../common/images/xiaomicc.jpg')} alt=""/></a>
                    <a href=""><img src={require('../../common/images/huanxin.jpg')} alt=""/></a>
                    <a href=""><img src={require('../../common/images/zhuanqu.jpg')} alt=""/></a>
                    <a href=""><img src={require('../../common/images/miaosha.png')} alt/>></a>
                    <a href=""><img src={require('../../common/images/bijiben.png')} alt=""/></a>
                    <a href=""><img src={require('../../common/images/remai.png')} alt=""/></a>
                    <a href=""><img src={require('../../common/images/kongt.jpg')} alt=""/></a>
                    <a href=""><img src={require('../../common/images/fenka.png')} alt=""/></a>

                {/*<div className={'nav_img'}>*/}
                {/*   */}
                {/*</div>*/}
            </div>
        );
    }
}

export default Index;
